import React, { useState } from 'react';
import ReactMarkdown from 'markdown-to-jsx';
import './styles.css';

const Markdown = () => {
  const [markDown,setMarkDown] = useState("Welcome");
  return (
    <>
        <header>
          <b>Markdown-Previewer</b>
        </header>

        <div className="center-div"> 

          <textarea className="left-side" value={markDown} onChange={(e) => setMarkDown(e.target.value)}></textarea>


          <div className="right-side">
            <ReactMarkdown>{markDown}</ReactMarkdown>
            <></>
            </div>

        <footer>
          <p>For queeries contact on:<a href="https://github.com/RahulNegi7" target="_blank"><img src="https://skillicons.dev/icons?i=github" height="30px"></img></a>
          <a href="https://www.linkedin.com/in/rahul-negi-961b88275/" target="_blank"><img src="https://skillicons.dev/icons?i=linkedin" height="30px"></img></a></p>
        </footer> 

        </div>
    </>  
  );
};

export default Markdown;
